import React, {useState, useEffect} from 'react'
import "./Nav.css"
import logo from '../Img/diamoncik.png'
import {Link} from 'react-router-dom'
import {BsList} from 'react-icons/bs'

const Nav = () => {

  const [toggleMenu, setToggleMenu] = useState(false)
  const [screenWidth, setscreenWidth] = useState(window.innerWidth);

  const toggleNav = () =>{
    setToggleMenu(!toggleMenu)
  }
  useEffect(() => {
    const changeWidth = () =>{
        setscreenWidth(window.innerWidth)
    }
    window.addEventListener('resize',changeWidth)
  }, []);

  return (
    <div className='nav'>
       {(toggleMenu  || screenWidth > 500) && (
         <div>
            <Link className='items' to='/paznokcie'>Paznokcie</Link>
            <Link className='items' to='/makepu'>makepu</Link>
            <Link  className='items'to='/promocje'>promocje</Link>
            <Link  className='items'to='/' exact><img className='logo' alt='logo' src={logo} /></Link>
            <Link  className='items'to='/onas'>o nas</Link>
            <Link  className='items'to='/cennik'>cennik</Link>
            <Link  className='items'to='/kontakt'>kontakt</Link>
            </div>
            )}
            <button onClick={toggleNav} className='icon-small'><BsList /></button>
    </div>
  
  )
}

export default Nav
